import React, { Component } from 'react'

export class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       number: 0
    }
  }

  click = () => {
  this.setState(prevState => {return {number: prevState.number+1}} )}

  render() {
    const {number} = this.state
    return (
      <div>
        <button onClick={this.click}>{number}</button>
      </div>
    )
  }
}

export default App
