import React, { useContext } from 'react';
import styles from '../../../utils/css/PizzaCard.module.css';
import axios from 'axios';
import { Context } from '../../../pages/MainPage/MainPage';
import { useDispatch, useSelector } from 'react-redux';
import { setPizzaCard, setAddPizza, setSelectedOption, setSelectedOptions } from '../../../Redux/slices/Pizza';

function PizzaCard() {
  const dispatch = useDispatch()
  const PizzaCards = useSelector(state => state.Pizza.PizzaCards)
  const selectedOption = useSelector(state => state.Pizza.selectedOption)
  const selectedOptions = useSelector(state => state.Pizza.selectedOptions)
  const search = useSelector((state) => state.filter.search)

  React.useEffect(() => {
    axios
      .get('https://demo5518646.mockable.io/PizzaCard')
      .then((res) => {
        dispatch(setPizzaCard(res.data.card));
      });
  }, []);
  const { AddBtn, addBtn } = useContext(Context)

  const handleOptionChange = (cardId, selectedOption) => {
    // Обновление состояния выбранной опции для конкретной карты
    setSelectedOptions(prevState => ({
      ...prevState,
      [cardId]: selectedOption,
    }));
  }

  const cardList = PizzaCards ? PizzaCards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];
  return (
    <div>
      <h1 className={styles.MenuPizza}>Все Пиццы</h1>
      <div className={styles.inline}>
        {cardList.map((card, i) => (
          <div className={styles.card} key={card.id}  >
            <img src={card.img} className={styles.imgPizza} alt="logo" />
            <h1>{card.name}</h1>

            <div className={styles.radioContainer}>

                <label className={`${styles.radioType} ${selectedOption === card.type[0] ? styles.ActiveTradition : styles.radioType}`}>
                  <input className={styles.InputRadio} type="radio" value={card.type[0]}  checked={selectedOptions[card.id] === card.type[0]}
                  onChange={() => handleOptionChange(card.id, card.type[0])}/>
                  {card.type[0]}
                </label>

                <label className={`${styles.radioType} ${selectedOption === card.type[1] ? styles.ActiveThin : styles.radioType}`} >
                  <input className={styles.InputRadio} type="radio" value={card.type[1]}  checked={selectedOptions[card.id] === card.type[1]}
                   onChange={() => handleOptionChange(card.id, card.type[0])}/>
                  {card.type[1]}
                </label>

                {/* <label className={styles.radioSize} >
                  <input className={styles.InputRadio} type="radio" value={card.sizes.small} checked={selectedOption === card.sizes.small}
                    onChange={handleOptionChange} />
                  {card.sizes.small}
                </label>
                <label className={styles.radioSize} >
                  <input className={styles.InputRadio} type="radio" value={card.sizes.medium} checked={selectedOption === card.sizes.medium}
                    onChange={handleOptionChange} />
                  {card.sizes.medium}
                </label>
                <label className={styles.radioSize} >
                  <input className={styles.InputRadio} type="radio" value={card.sizes.large} checked={selectedOption === card.sizes.large}
                    onChange={handleOptionChange} />
                  {card.sizes.large}
                </label>
               */}



              {/* <button  onClick={() => changeButton === 1? styles.tradition : styles.ActiveTradition} >
                  {card.type[0]}
                </button>

                <button onClick={() => changeButton === 2? styles.thin : styles.ActiveThin} className={styles.ActiveTradition }>
                  {card.type[1]}
                </button> */}

              {/* <button>{card.sizes.small}</button>
              <button>{card.sizes.medium}</button>
              <button>{card.sizes.large}</button> */}
            </div>

            <h4>{card.desc}</h4>
            <h2>{card.price}</h2>
            <button onClick={AddBtn} className={styles.btn}>Добавить {addBtn}</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PizzaCard;
