import React from 'react'
import styles from '../'
import Pizza from '../Test.json'

function Test() {
    const [activeTypeState, setActiveTypeState] = React.useState(Pizza.map(() => 0)); 
    const ChooseType = (index) => {
        setActiveTypeState(index);
      };
     
    return (
    <div>
      {Pizza.map((card, index) => (
         <div className={styles.categories}>
                <ul className={styles.typeSelector} >
                  <li className={activeTypeState === 0 ? styles.ActiveTradition : styles.tradition}
                    onClick={() => ChooseType(index)}>
                    {card.tradition}
                  </li>

                  <li className={activeTypeState === 1 ? styles.ActiveThin : styles.thin}
                    onClick={() => ChooseType(index)}>
                    {card.thin}
                  </li>
                </ul>

                <ul className={styles.sizeSelector}>
                  <li className={styles.TwentyFiveSm}>
                    {card.sizes.small}
                  </li>

                  <li className={styles.ThirtySm}>
                    {card.sizes.medium}
                  </li>

                  <li className={styles.ThirtyFiveSm}>
                    {card.sizes.large}
                  </li>

                </ul>
              </div>))}    
      
    </div>
  )
}

export default Test
