import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { setActiveType } from '../Redux/slices/Pizza';
import styles from './SizeOption.module.scss'

function SizeOption({ sizes, id }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = (selectedType) => {
    dispatch(setActiveType({ id, type: selectedType }));
  };
  return (
    <>
      {Object.values(sizes).map((sizeValue, index) => (
        <label className={styles.radioSize} key={sizeValue}
        onClick={() => handleTypeClick(sizeValue)}>
          <input className={styles.InputRadio} type="radio" value={sizeValue} checked={ActiveType === sizeValue}
           />
          {sizeValue}
        </label>
      ))}
    </>
  )
}

export default SizeOption
