import React from 'react';
import Header from '../Header/Header';
import Categorie from '../../components/Categories/categorie';
import ListCard from '../../components/PizzaCard/listCard';
import OftenOrder from '../../components/OftenOrder/ListMiniCard';
import Footer from '../Footer/Footer';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';


function MainPage() {
  const [cards, setCards] = React.useState([]);
  const [search, setSearch] = React.useState('');
  const [OftOrdCard, setOftOrdCard] = React.useState([]);

  const targetRef = React.useRef(null);
  const scrollToRef = () => {
    targetRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
       <>
          <Header />
          <Categorie scrollToRef={scrollToRef} />
          <OftenOrder />
          
            <Route path="/" element={<ListCard/>} />
         
          <Footer />
       </>
  );
}

export default MainPage;
