import {useState} from 'react'
import Text from './Text'
function ShowText() {
  const [ChangeText, setChangeText] = useState(false)

  return (
    <div>
      <button onClick={() => {setChangeText(!ChangeText)}}>Hover</button>
      {ChangeText && <Text />}
    </div>
  )
}

export default ShowText
