// Ваш текущий код компонента PizzaCard

import React, { useContext, useEffect } from 'react';
import styles from '../../../utils/css/PizzaCard.module.css';
import axios from 'axios';
import { Context } from '../../../pages/MainPage/MainPage';
import { useDispatch, useSelector } from 'react-redux';
import { setPizzaCard, setActiveType } from '../../../Redux/slices/Pizza';

function PizzaCard() {
  const dispatch = useDispatch();
  const PizzaCards = useSelector(state => state.Pizza.PizzaCards);
  const ActiveType = useSelector(state => state.Pizza.ActiveType);
  const search = useSelector((state) => state.filter.search);

  useEffect(() => {
    axios
      .get('https://demo5518646.mockable.io/PizzaCard')
      .then((res) => {
        dispatch(setPizzaCard(res.data.card));
      });
  }, []);

  const handleTypeClick = (typeOption, cardId) => {
    dispatch(setActiveType({ cardId, ActiveType: typeOption }));
  };

  const { AddBtn, addBtn } = useContext(Context);

  const cardList = PizzaCards ? PizzaCards.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];

  return (
    <div>
      <h1 className={styles.MenuPizza}>Все Пиццы</h1>
      <div className={styles.inline}>
        {cardList.map((card, i) => (
          <div className={styles.card} key={card.id}  >
            <img src={card.img} className={styles.imgPizza} alt="logo" />
            <h1>{card.name}</h1>

            <div className={styles.radioContainer}>
              {card.type.map(typeOption => (
                <label key={typeOption} className={ActiveType === typeOption ? styles.ActiveTradition : styles.radioType}
                  onClick={() => handleTypeClick(typeOption, card.id)}>
                  <input className={styles.InputRadio} type="radio" value={typeOption} checked={ActiveType === typeOption} />
                  {typeOption}
                </label>
              ))}
            </div>

            <h4>{card.desc}</h4>
            <h2>{card.price}</h2>
            <button onClick={AddBtn} className={styles.btn}>Добавить {addBtn}</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PizzaCard;
