import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setActiveType } from '../Redux/slices/Pizza';
import styles from './css/TypeOption.module.css';

function TypeOption({ type }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = (event, index) => {
    dispatch(setActiveType([...ActiveType.map((data, i) => i === index ? event.target.value : data)]));

  };
  React.useEffect(() => {
    console.log(ActiveType);
  }, [ActiveType]);



  return (
    <div>
      {type.map((card) => (
        <label key={card.id} className={ActiveType === card.id ? styles.ActiveTradition : styles.radioType}
          onClick={() => handleTypeClick(card.id)}>
          <input className={styles.InputRadio} type="radio" value={card.id} checked={ActiveType === card.id} />
          {card.name}
        </label>
      ))}
    </div>
  );

}

export default TypeOption;
