import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setActiveType } from '../Redux/slices/Pizza';
import styles from './css/TypeOption.module.css';

function TypeOption({ type, id }) {
  const dispatch = useDispatch();
  const ActiveType = useSelector(state => state.Pizza.ActiveType);

  const handleTypeClick = (typeOption) => {
    dispatch(setActiveType({ id, type: typeOption }));
  };
  
  
  return (
    <div>
      {type.map((typeOption) => (
        <label key={typeOption} className={ActiveType === id && ActiveType.type === typeOption ? styles.ActiveTradition : styles.radioType}
          onClick={handleTypeClick}>
          <input className={styles.InputRadio} type="radio" value={typeOption} checked={ActiveTyp}   onChange={handleTypeClick}/>
          {typeOption}
        </label>
      ))}
    </div>
  );

}

export default TypeOption;
