import React, { useContext } from 'react';
import styles from './cards.module.css';

function Cards(props) {
  const { cards } = props;
  const {search} = useContext()

  const cardList = cards.filter(card => card.name.toLowerCase().includes(search.toLowerCase()))


  return (
    <div className={styles.inline}>
      {cardList.map(card =>
        <div className={styles.card} key={card.id}  >
          <img src={card.img} className={styles.pizza} alt="logo" />
          <h1>{card.name}</h1>
          <h4>{card.desc}</h4>
          <h2>{card.price}</h2>
          <button className={styles.btn}>Добавить</button>
        </div>)}
    </div>
  );
}

export default Cards;
