import { createSlice } from "@reduxjs/toolkit";

export const NapitkiCardSlice = createSlice({
  name: "Snacks",
  initialState: {
    NapitkiCards: [],
    AddNapitki: []
  },
  reducers: {
    setNapitkiCards(state, action) {
      state.NapitkiCards = action.payload;
    },
    setAddNapitki(state, action) {
     state.AddNapitki.push(action.payload) 
    }
  },
});

export const { setSnacksCards, setAddSnacks } = NapitkiCardSlice.actions;
export default NapitkiCardSlice.reducer;
