import React from 'react';
import axios from 'axios';
import styles from '../css/Drinks.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { setAddDrink, setDrinkCards } from '../../../Redux/slices/Drink';


function DrinksCard({img, name, desc, price}) {
    const dispatch = useDispatch()
    const Drinks = useSelector(state => state.Drink.DrinkCards)

    React.useEffect(() => {
        axios
          .get('https://demo5518646.mockable.io/DrinksCard')
          .then((res) => {
            dispatch(setDrinkCards(res.data));
          });
    }, [dispatch, Drinks]);

    const onClickAdd = () => {
      const pushDrinks = {img, name, desc, price}
      dispatch(setAddDrink(pushDrinks))
    }

    const search = useSelector((state) => state.filter.search)


    const DrinksCard = Drinks ? Drinks.filter(card => card.name.toLowerCase().includes(search.toLowerCase())) : [];
    return (
      <div>
        <h1 className={styles.MenuDrinks}>Все Напитки</h1>
        <div className={styles.inline}>
          {DrinksCard.map((card) => (
            <div className={styles.card}  key={card.id}  >
              <img src={card.img} className={styles.imgPizza} alt="logo" />
              <div className={styles.PizzaName}>
                <h1>{card.name}</h1>
              </div>
              <div>
                <h4 className={styles.desc}>{card.desc}</h4>
              </div>
              <div>
                <div className={styles.price}>
                  <h2>{card.price}</h2>
                </div>
                <button onClick={onClickAdd} className={styles.btn}> Добавить</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    ); 
}

export default DrinksCard;
