import React, { Component } from "react";
import { UserConsumer } from "./Context";
import '../App.css'

class Example extends Component {
  render() {
    return (
      <div>
        <UserConsumer>
          {(name) => {
            return <h1 className="text">Hello! {name} Practice</h1>
          }}
        </UserConsumer>
      </div>
    );
  }
}

export default Example;
