import React, { Component } from 'react'

class App extends Component {
 constructor(props) {
   super(props)

   this.state = {
     number: 0
   }
 }

  mouseOver = () => {
  this.setState(prevState => {return {number: prevState.number+1}})}

  render() {
    const {number} = this.state
    return (
      <div>
       <h1 onMouseOver={this.mouseOver}>Hello {number}</h1>        
      </div>
    )
  }
}

export default App
